public static boolean findElement(TreeNode root, int val) {
    // Base case: if root is null, the element is not found
    if (root == null) {
        return false;
    }

    // If the root's value matches the element, return true
    if (root.val == val) {
        return true;
    }

    // If the element is less than the root's value, search in the left subtree
    if (val < root.val) {
        return findElement(root.left, val);
    }

    // If the element is greater than the root's value, search in the right subtree
    if (val > root.val) {
        return findElement(root.right, val);
