    // Initialize the hash code to a non-negative integer
    int hashCode = 0;

    // Loop through each byte in the ByteBuffer and update the hash code
    for (int i = 0; i < key.capacity(); i++) {
        hashCode = 31 * hashCode + key.get(i);
    }

    // Ensure that the hash code is non-negative
    hashCode = Math.abs(hashCode);

    // Calculate the bucket index using the modulo operator
    int bucketIndex = hashCode % numBuckets;

