public static String getRelativePath(String parentPath, String absolutePath) {
    // Split the parent and absolute paths into segments
    String[] parentSegments = parentPath.split("/");
    String[] absoluteSegments = absolutePath.split("/");

    // Find the common prefix between the two paths
    int prefixLength = 0;
    while (prefixLength < parentSegments.length
            && prefixLength < absoluteSegments.length
            && parentSegments[prefixLength].equals(absoluteSegments[prefixLength])) {
        prefixLength++;
    }

    // Build the relative path by appending segments from the absolute path
    StringBuilder relativePath = new StringBuilder();
    for (int i = prefixLength; i < absoluteSegments.length; i++) {
        if (i > prefixLength) {
            relativePath.append("/");
        }
        relativePath.append(absoluteSegments[i]);
    }

    return relativePath.toString();
}

