    public static byte[] convert(List<String> mnemonic, String passphrase) {
        String joinedMnemonic = String.join(" ", mnemonic);
        byte[] salt = ("mnemonic" + passphrase).getBytes();
        int iterations = 2048;
        int keyLength = 512;
        try {
            return PBKDF2SHA512.derive(joinedMnemonic, salt, iterations, keyLength);
        } catch (Exception e) {
            throw new RuntimeException("Error converting mnemonic to seed", e);
        }
    }
