class PBKDF2SHA512 {
    public static byte[] derive(String mnemonic, byte[] salt, int iterations, int keyLength) throws Exception {
        byte[] mnemonicBytes = mnemonic.getBytes("UTF-8");
        SecretKeyFactory secretKeyFactory = SecretKeyFactory.getInstance("PBKDF2WithHmacSHA512");
        PBEKeySpec spec = new PBEKeySpec(new String(mnemonicBytes, "UTF-8").toCharArray(), salt, iterations, keyLength);
        SecretKey secretKey = secretKeyFactory.generateSecret(spec);
        return secretKey.getEncoded();
    }
