import org.ice4j.ice.harvest.TurnCandidateHarvester.Credentials;
import org.ice4j.ice.harvest.TurnCandidateHarvester.MappedAddress;

import java.net.InetAddress;
import java.util.ArrayList;
import java.util.List;

public class TurnClient {
    private static final int DEFAULT_STUN_PORT = 3478;
    private static final int DEFAULT_TURN_PORT = 3478;

    public static void connectToTurnServer(
            String turnServer,
            String username,
            String password,
            String localAddress,
            int localPort) throws Exception {

        // Create the list of harvesters
        List<StunCandidateHarvester> harvesters = new ArrayList<>();
        StunCandidateHarvester stunHarvester = new StunTurnCandidateHarvester(
                new InetSocketAddress(turnServer, DEFAULT_STUN_PORT),
                localAddress, DEFAULT_STUN_PORT);
        harvesters.add(stunHarvester);

        // Create the TURN harvester
        Credentials credentials = new Credentials(username, password);
        MappedAddress mappedAddress = new MappedAddress(
                InetAddress.getByName(localAddress), localPort);
        TurnCandidateHarvester turnHarvester = new TurnCandidateHarvester(
                new InetSocketAddress(turnServer, DEFAULT_TURN_PORT),
                credentials, mappedAddress);
        harvesters.add(turnHarvester);

        // Create the StunTurnClient and initialize it with the harvesters
        StunTurnClient stunTurnClient = new StunTurnClient();
        stunTurnClient.initialize(harvesters);

        // Perform connectivity checks to the TURN server
        stunTurnClient.start();
    }
}

