public int getRemoteExitCode(Process process) throws IOException, InterruptedException {
    int exitCode = -1;
    InputStream inputStream = process.getInputStream();
    BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream));

    // Wait for the command to finish and consume all data from the remote side
    process.waitFor();
    while (reader.ready()) {
        String line = reader.readLine();
        // Process the output of the command, if needed
        // ...
    }

    // Check if the remote command has an exit code/status
    try {
        exitCode = process.exitValue();
    } catch (IllegalThreadStateException e) {
        // The process has not terminated yet, so no exit code is available
        // ...
    }

    return exitCode;
}

