import java.net.InetSocketAddress;
import java.util.Arrays;

public static Proxy getProxy(URI uri) {
    String nonProxyHosts = System.getProperty("http.nonProxyHosts");

    if (nonProxyHosts != null) {
        String[] nonProxyHostsList = nonProxyHosts.split("\\|");

        for (String nonProxyHost : nonProxyHostsList) {
            if (uri.getHost().matches(nonProxyHost.trim())) {
                return Proxy.NO_PROXY;
            }
        }
    }

    ProxySelector defaultProxySelector = ProxySelector.getDefault();
    try {
        return defaultProxySelector.select(uri).get(0);
    } catch (Exception e) {
        return Proxy.NO_PROXY;
    }
}

