public class CipherSuitesFilter {

  public static String[] getCipherSuitesFilter() {
    SSLServerSocketFactory factory = (SSLServerSocketFactory) SSLServerSocketFactory.getDefault();
    String[] enabledCipherSuites = factory.getDefaultCipherSuites();
    String[] supportedCipherSuites = factory.getSupportedCipherSuites();
    String[] disabledCipherSuites = {"TLS_RSA_WITH_AES_128_CBC_SHA", "TLS_RSA_WITH_AES_256_CBC_SHA"};

    String[] filteredCipherSuites = new String[enabledCipherSuites.length];
    int index = 0;

    for (String enabledCipherSuite : enabledCipherSuites) {
      boolean isSupported = false;
      for (String supportedCipherSuite : supportedCipherSuites) {
        if (enabledCipherSuite.equals(supportedCipherSuite)) {
          isSupported = true;
          break;
        }
      }
      boolean isEnabled = true;
      for (String disabledCipherSuite : disabledCipherSuites) {
        if (enabledCipherSuite.equals(disabledCipherSuite)) {
          isEnabled = false;
          break;
        }
      }
      if (isSupported && isEnabled) {
        filteredCipherSuites[index++] = enabledCipherSuite;
      }
    }

    String[] result = new String[index];
    System.arraycopy(filteredCipherSuites, 0, result, 0, index);
    return result;
  }

}

