import java.math.BigInteger;
import java.util.Arrays;

public class P2SHAddressParser {

    /**
     * Returns the first byte of a base58 encoded P2SH address.
     *
     * @param p2shAddress a base58 encoded P2SH address
     * @return the first byte of the P2SH address
     */
    public static byte getFirstByteFromP2SHAddress(String p2shAddress) {
        byte[] decodedBytes = Base58.decode(p2shAddress);

        // Skip the first byte (version byte) and the last four bytes (checksum)
        byte[] hashBytes = Arrays.copyOfRange(decodedBytes, 1, decodedBytes.length - 4);

        // The first byte of the hash is the P2SH address type
        return hashBytes[0];
    }

    // Helper class for decoding base58 strings
    private static class Base58 {
        private static final BigInteger BASE = BigInteger.valueOf(58);
        private static final String ALPHABET = "123456789ABCDEFGHJKLMNPQRSTUVWXYZabcdefghijkmnopqrstuvwxyz";
        private static final int[] INDEXES = new int[128];

        static {
            Arrays.fill(INDEXES, -1);
            for (int i = 0; i < ALPHABET.length(); i++) {
                INDEXES[ALPHABET.charAt(i)] = i;
            }
        }

        public static byte[] decode(String input) {
            byte[] input58 = new byte[input.length()];
            for (int i = 0; i < input.length(); ++i) {
                char c = input.charAt(i);
                int digit = c < 128 ? INDEXES[c] : -1;
                if (digit < 0) {
                    throw new IllegalArgumentException("Illegal character " + c + " at position " + i);
                }
                input58[i] = (byte) digit;
            }

            BigInteger value = BigInteger.ZERO;
            for (int i = 0; i < input58.length; i++) {
                value = value.multiply(BASE).add(BigInteger.valueOf(input58[i]));
            }

            byte[] bytes = value.toByteArray();

            // Check if there's a leading zero byte
            if (bytes.length > 1 && bytes[0] == 0) {
                bytes = Arrays.copyOfRange(bytes, 1, bytes.length);
            }

            return bytes;
        }
    }
}

