import java.io.*;

public class HexDecoder {

    /**
     * Decodes a Hex-encoded string and writes the decoded data to a given output stream.
     * Whitespace characters will be ignored.
     *
     * @param hexString The Hex-encoded string to decode.
     * @param outputStream The output stream to write the decoded data to.
     */
    public static void decodeHex(String hexString, OutputStream outputStream) throws IOException {
        hexString = hexString.replaceAll("\\s", ""); // Remove whitespace characters
        byte[] bytes = new byte[hexString.length() / 2];
        for (int i = 0; i < bytes.length; i++) {
            int hex = Integer.parseInt(hexString.substring(i * 2, i * 2 + 2), 16);
            bytes[i] = (byte) hex;
        }
        outputStream.write(bytes);
    }
}

