public static double naiveSimilarityScore(String s1, String s2) {
    // Convert the strings to lowercase to make the comparison case-insensitive
    s1 = s1.toLowerCase();
    s2 = s2.toLowerCase();

    int matchedChars = 0;
    // Iterate over each character in s1 and check if it exists in s2
    for (int i = 0; i < s1.length(); i++) {
        if (s2.indexOf(s1.charAt(i)) >= 0) {
            matchedChars++;
        }
