import java.nio.ByteBuffer;
import java.util.zip.CRC32;

public class RUDPSegment {
    private static final int CHECKSUM_LENGTH = 4; // 4 bytes for CRC32 checksum

    // other fields and methods

    public static boolean isChecksumCorrect(ByteBuffer buffer) {
        int position = buffer.position();
        int limit = buffer.limit();

        // calculate checksum over the segment (excluding the last 4 bytes which hold the checksum itself)
        CRC32 crc32 = new CRC32();
        buffer.limit(limit - CHECKSUM_LENGTH);
        crc32.update(buffer);

        // compare calculated checksum with the checksum held in the buffer
        buffer.limit(limit);
        buffer.position(limit - CHECKSUM_LENGTH);
        long expectedChecksum = buffer.getInt() & 0xffffffffL; // mask to prevent sign extension
        long calculatedChecksum = crc32.getValue();
        return expectedChecksum == calculatedChecksum;

        // reset buffer position and limit to original values
        buffer.limit(limit);
        buffer.position(position);
    }
}

