public static double calculateDistance(double latMax, double latMin) {
    double earthRadius = 6371; // Radius of the earth in kilometers
    double latMaxRadians = Math.toRadians(latMax);
    double latMinRadians = Math.toRadians(latMin);
    double latDiffRadians = latMaxRadians - latMinRadians;

    double a = Math.pow(Math.sin(latDiffRadians / 2), 2) +
               Math.cos(latMinRadians) * Math.cos(latMaxRadians) *
               Math.pow(Math.sin((0.0 - latMinRadians) / 2), 2);

    double c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    double distance = earthRadius * c;

    return distance;
}

