public static char[] reverseCharArray(char[] arr) {
    int left = 0;
    int right = arr.length - 1;

    while (left < right) {
        // Swap the left and right elements
        char temp = arr[left];
        arr[left] = arr[right];
        arr[right] = temp;

        // Move the left and right pointers inward
        left++;
        right--;
    }

    return arr;
}

