public List<byte[]> processBuffer(byte[] buffer, int packetSize) {
    List<byte[]> packets = new ArrayList<>();
    int bufferSize = buffer.length;

    // Check if there is enough data to create a full packet
    if (bufferSize >= packetSize) {
        int numPackets = bufferSize / packetSize;

        // Split the buffer into packets of the specified size
        for (int i = 0; i < numPackets; i++) {
            byte[] packet = new byte[packetSize];
            System.arraycopy(buffer, i * packetSize, packet, 0, packetSize);
            packets.add(packet);
        }

        // Remove the processed data from the buffer
        int remainingDataSize = bufferSize % packetSize;
        if (remainingDataSize > 0) {
            byte[] remainingData = new byte[remainingDataSize];
            System.arraycopy(buffer, numPackets * packetSize, remainingData, 0, remainingDataSize);
            buffer = remainingData;
        } else {
            buffer = new byte[0];
        }
    }

    // Return the list of packets and the remaining buffer
    return packets;
}

