```java
public static double calculateUnbiasedSampleVariance(double[] values) {
    int n = values.length;
    if (n <= 1) {
        throw new IllegalArgumentException("Sample size must be greater than one.");
    }

    double sum = 0.0;
    double sumSquared = 0.0;
    for (double value : values) {
        if (Double.isNaN(value) || Double.isInfinite(value)) {
            return Double.NaN;
        }
        sum += value;
        sumSquared += value * value;
    }

    double sampleMean = sum / n;
    double numerator = sumSquared - n * sampleMean * sampleMean;
    double denominator = n - 1;

    return numerator / denominator;
