public static void debugXmlFeed(String url) {
    try {
        // Create a new URL object and open a connection to the URL
        URL feedUrl = new URL(url);
        HttpURLConnection connection = (HttpURLConnection) feedUrl.openConnection();

        // Set the request method and user agent
        connection.setRequestMethod("GET");
        connection.setRequestProperty("User-Agent", "Mozilla/5.0");

        // Get the response code and input stream from the connection
        int responseCode = connection.getResponseCode();
        InputStream inputStream = connection.getInputStream();

        // Create a new DocumentBuilderFactory and DocumentBuilder to parse the XML
        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        DocumentBuilder builder = factory.newDocumentBuilder();

        // Parse the XML feed and get the root element
        Document document = builder.parse(inputStream);
        Element rootElement = document.getDocumentElement();

        // Print the root element and its child nodes
        System.out.println("XML feed for " + url + ":");
        printNode(rootElement, 0);

        // Close the input stream and connection
        inputStream.close();
        connection.disconnect();
    } catch (MalformedURLException e) {
        System.out.println("Error: Invalid URL");
    } catch (IOException e) {
        System.out.println("Error: Unable to connect to URL");
    } catch (ParserConfigurationException e) {
        System.out.println("Error: Unable to create XML parser");
    } catch (SAXException e) {
        System.out.println("Error: Unable to parse XML");
    }
}

private static void printNode(Node node, int level) {
    // Print the node's name and attributes
    for (int i = 0; i < level; i++) {
        System.out.print("  ");
    }
    System.out.print(node.getNodeName());
    NamedNodeMap attributes = node.getAttributes();
    for (int i = 0; i < attributes.getLength(); i++) {
        Node attribute = attributes.item(i);
        System.out.print(" " + attribute.getNodeName() + "=\"" + attribute.getNodeValue() + "\"");
    }
    System.out.println();

    // Print the node's child nodes recursively
    NodeList childNodes = node.getChildNodes();
    for (int i = 0; i < childNodes.getLength(); i++) {
        Node childNode = childNodes.item(i);
        if (childNode.getNodeType() == Node.ELEMENT_NODE) {
            printNode(childNode, level + 1);
        }
    }
}

