import java.sql.*;

public class JoinDistinctCountEstimator {

  // Connection to the database
  private Connection connection;

  // Constructor to initialize the connection
  public JoinDistinctCountEstimator(Connection connection) {
    this.connection = connection;
  }

  /**
   * Estimates the distinct number of field values in the join.
   * @param joinTable The name of the table resulting from the join
   * @param joinField The name of the field(s) being joined on, separated by commas
   * @param whereClause The WHERE clause to be applied to the underlying query
   * @return The estimated distinct count of the join field(s)
   */
  public int estimateDistinctCount(String joinTable, String joinField, String whereClause) {
    try {
      // Construct the underlying query
      String query = "SELECT DISTINCT " + joinField + " FROM " + joinTable;
      if (whereClause != null && !whereClause.isEmpty()) {
        query += " WHERE " + whereClause;
      }

      // Prepare the statement and execute the query
      PreparedStatement statement = connection.prepareStatement(query);
      ResultSet resultSet = statement.executeQuery();

      // Calculate the estimated distinct count
      int count = 0;
      while (resultSet.next()) {
        count++;
      }
      return count;

    } catch (SQLException e) {
      // Handle any errors that may occur
      e.printStackTrace();
      return -1;
    }
  }
}

