public static int getPreferredGroupLength(List<Integer> list) {
    int[] frequency = new int[list.size()];
    int maxFrequency = 0;

    // Calculate the frequency of each element in the list
    for (int i = 0; i < list.size(); i++) {
        int count = 1;
        for (int j = i + 1; j < list.size(); j++) {
            if (list.get(i).equals(list.get(j))) {
                count++;
            }
        }
        frequency[i] = count;
        maxFrequency = Math.max(maxFrequency, count);
    }

    // Find the smallest group size with the highest frequency
    int preferredGroupLength = Integer.MAX_VALUE;
    for (int i = 0; i < list.size(); i++) {
        if (frequency[i] == maxFrequency) {
            preferredGroupLength = Math.min(preferredGroupLength, frequency[i]);
        }
    }

    return preferredGroupLength;
}

