import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.util.List;

public class FileRenamer {
    public void renameFiles(List<String> fileLocations, String newFilename) {
        for (String fileLocation : fileLocations) {
            try {
                // Get the file from the file location
                File file = new File(fileLocation);
                if (!file.exists()) {
                    // If the file doesn't exist, skip it and continue with the loop
                    continue;
                }

                // Get the parent directory of the file
                File parentDir = file.getParentFile();

                // Construct the new file name with the same extension as the original file
                String extension = "";
                int extensionIndex = file.getName().lastIndexOf('.');
                if (extensionIndex >= 0) {
                    extension = file.getName().substring(extensionIndex);
                }
                String newFilenameWithExtension = newFilename + extension;

                // Construct the new file path
                Path newFilePath = parentDir.toPath().resolve(newFilenameWithExtension);

                // Rename the file
                Files.move(file.toPath(), newFilePath);
            } catch (IOException e) {
                // Handle any exceptions that occur during the file renaming process
                System.err.println("Error renaming file " + fileLocation + ": " + e.getMessage());
            }
        }
    }
}

