public static Key removePrivateKey(Key key) {
    if (key instanceof RSAPrivateKey) {
        // if it's an RSA private key, return the corresponding public key
        RSAPrivateKey privateKey = (RSAPrivateKey) key;
        RSAPublicKeySpec publicKeySpec = new RSAPublicKeySpec(privateKey.getModulus(), privateKey.getPublicExponent());
        try {
            KeyFactory keyFactory = KeyFactory.getInstance("RSA");
            return keyFactory.generatePublic(publicKeySpec);
        } catch (NoSuchAlgorithmException | InvalidKeySpecException e) {
            throw new IllegalArgumentException("Invalid key format", e);
        }
    } else if (key instanceof ECPrivateKey) {
        // if it's an EC private key, return the corresponding public key
        ECPrivateKey privateKey = (ECPrivateKey) key;
        ECPublicKeySpec publicKeySpec = new ECPublicKeySpec(privateKey.getParams().getGenerator(), privateKey.getParams());
        try {
            KeyFactory keyFactory = KeyFactory.getInstance("EC");
            return keyFactory.generatePublic(publicKeySpec);
        } catch (NoSuchAlgorithmException | InvalidKeySpecException e) {
            throw new IllegalArgumentException("Invalid key format", e);
        }
    } else {
        // if it's not a private key, return the same instance
        return key;
    }
}

