public class PaymentChannelExample {

    private PaymentChannelClientConnection connection;

    // Constructor that initializes the PaymentChannelClientConnection object
    public PaymentChannelExample(PaymentChannelClientConnection connection) {
        this.connection = connection;
    }

    public CompletableFuture<Void> openChannel() {
        CompletableFuture<Void> future = new CompletableFuture<>();

        // Call openPaymentChannel method on the PaymentChannelClientConnection object
        connection.openPaymentChannel((clientConnection, serverConnection) -> {
            // Channel is successfully opened, complete the future
            future.complete(null);

            // Increment payment on the channel
            clientConnection.incrementPayment(BigInteger.valueOf(1000));
        }, throwable -> {
            // Error occurred, complete the future exceptionally
            future.completeExceptionally(throwable);
        });

        return future;
    }

    public static void main(String[] args) {
        PaymentChannelClientConnection connection = new PaymentChannelClientConnection();

        PaymentChannelExample example = new PaymentChannelExample(connection);
        CompletableFuture<Void> future = example.openChannel();

        try {
            future.get(); // Wait for the future to complete
        } catch (InterruptedException | ExecutionException e) {
            e.printStackTrace();
        }
    }
}

