public void makeGraphAPIRequest(String parameters, Callback callback) {
    new Thread(() -> {
        try {
            // Construct the URL with the given parameters
            String urlString = "https://graph.facebook.com/v13.0/" + parameters;
            URL url = new URL(urlString);

            // Open a connection to the URL
