        double middle = (lowerBound + upperBound) / 2.0;

        while (Math.abs(equation.apply(middle)) > epsilon && lowerBound <= upperBound) {
            if (equation.apply(lowerBound) * equation.apply(middle) < 0) {
                upperBound = middle;
            } else {
                lowerBound = middle;
            }
            middle = (lowerBound + upperBound) / 2.0;
        }

        return middle;
    }
}

