import java.security.*;
import java.security.spec.*;
import java.util.*;
import javax.xml.bind.*;

public static byte[] createScriptSig(byte[] publicKeyBytes, byte[] signature) throws Exception {
    // Decode public key bytes to get the public key
    X509EncodedKeySpec publicKeySpec = new X509EncodedKeySpec(publicKeyBytes);
    KeyFactory keyFactory = KeyFactory.getInstance("EC");
    PublicKey publicKey = keyFactory.generatePublic(publicKeySpec);

    // Create DER encoded signature
    Signature ecdsa = Signature.getInstance("SHA256withECDSA");
    ecdsa.initVerify(publicKey);
    ecdsa.update("message".getBytes()); // Replace "message" with the actual message being signed
    boolean isValid = ecdsa.verify(signature);

    ByteArrayOutputStream scriptSigStream = new ByteArrayOutputStream();

    // Push signature onto the stack
    scriptSigStream.write(signature.length);
    scriptSigStream.write(signature);

    // Push public key onto the stack
    scriptSigStream.write(publicKeyBytes.length);
    scriptSigStream.write(publicKeyBytes);

    // Convert scriptSig to byte array
    byte[] scriptSig = scriptSigStream.toByteArray();

    return scriptSig;
}

