import java.sql.Connection;
import java.sql.SQLException;

public class ConnectionManager {
  // Define the time limit for an idle connection (in milliseconds)
  private static final long IDLE_TIME_LIMIT = 10 * 60 * 1000; // 10 minutes

  // Method to close idle connections
  public static void closeIdleConnections(Connection[] connections) {
    long currentTime = System.currentTimeMillis();
    for (Connection connection : connections) {
      try {
        // Check if the connection is idle
        if (currentTime - connection.getLastUsedTime() > IDLE_TIME_LIMIT) {
          // Close the idle connection
          connection.close();
        }
      } catch (SQLException e) {
        System.err.println("Error closing idle connection: " + e.getMessage());
      }
    }
  }
}

