import java.util.List;

public class AnnotationScanner {

    public static List<Class<?>> scanForAnnotation(List<String> basePackages, Class<? extends Annotation> annotation) {
        List<Class<?>> annotatedClasses = new ArrayList<>();
        for (String basePackage : basePackages) {
            for (Class<?> clazz : getClasses(basePackage)) {
                if (clazz.isAnnotationPresent(annotation)) {
                    annotatedClasses.add(clazz);
                }
            }
        }
        return annotatedClasses;
    }

    private static List<Class<?>> getClasses(String packageName) {
        List<Class<?>> classes = new ArrayList<>();
        try {
            ClassLoader classLoader = Thread.currentThread().getContextClassLoader();
            String path = packageName.replace('.', '/');
            for (var resource : classLoader.getResources(path)) {
                if (resource.toString().endsWith(".class")) {
                    String className = resource.toString().replace(".class", "").replace('/', '.');
                    Class<?> clazz = Class.forName(className);
                    classes.add(clazz);
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
        return classes;
    }

}

