public static boolean isValidIPv6(String ipAddress) {
    // Split the input string into an array of 8 segments
    String[] segments = ipAddress.split(":");

    // IPv6 addresses must have exactly 8 segments
    if (segments.length != 8) {
        return false;
    }

    // Iterate over each segment and validate it
    for (String segment : segments) {
        // Segment must not be empty
        if (segment.isEmpty()) {
            return false;
        }

        // Segment must not be longer than 4 characters
        if (segment.length() > 4) {
            return false;
        }

        // Segment must only contain hexadecimal characters
        for (int i = 0; i < segment.length(); i++) {
            char c = segment.charAt(i);
            if (!(Character.isDigit(c) || (c >= 'a' && c <= 'f') || (c >= 'A' && c <= 'F'))) {
                return false;
            }
        }
    }

    return true;
}

