import Jama.Matrix;

public class MatrixDecomposition {

  public static void main(String[] args) {
    double[][] A = {{1, 2, 3}, {4, 5, 6}, {7, 8, 9}};
    Matrix matrixA = new Matrix(A);

    // QR decomposition
    Matrix[] QR = matrixA.qr();
    Matrix Q = QR[0];
    Matrix R = QR[1];

    // Extract H and Q
    Matrix H = R.transpose().times(Q.transpose());
    Q = Q.transpose();

    // Recompute A using H and Q
    Matrix recomputedA = Q.times(H);

    System.out.println("Original matrix A:");
    matrixA.print(3, 2);
    System.out.println("Matrix H:");
    H.print(3, 2);
    System.out.println("Matrix Q:");
    Q.print(3, 2);
    System.out.println("Recomputed matrix A:");
    recomputedA.print(3, 2);
  }
}

