import java.util.ArrayList;
import org.json.JSONArray;
import org.json.JSONObject;

public class PubMedSearch {

  public static ArrayList<String> getPMIDs(String searchTerm) throws Exception {

    String url = "https://eutils.ncbi.nlm.nih.gov/entrez/eutils/esearch.fcgi?db=pubmed&term=" + searchTerm;

    // Connect to PubMed database and retrieve JSON response
    URL obj = new URL(url);
    HttpURLConnection con = (HttpURLConnection) obj.openConnection();
    con.setRequestMethod("GET");
    BufferedReader in = new BufferedReader(new InputStreamReader(con.getInputStream()));
    String inputLine;
    StringBuffer response = new StringBuffer();
    while ((inputLine = in.readLine()) != null) {
      response.append(inputLine);
    }
    in.close();

    // Parse JSON response to retrieve PMIDs
    ArrayList<String> pmids = new ArrayList<String>();
    JSONObject jsonObj = new JSONObject(response.toString());
    JSONArray jsonArray = jsonObj.getJSONObject("esearchresult").getJSONArray("idlist");
    for (int i = 0; i < jsonArray.length(); i++) {
      pmids.add(jsonArray.getString(i));
    }

    return pmids;
  }
}

