import java.time.Instant;
import java.util.Date;

public class DurationString {

    public static String getDurationString(Date date) {
        Instant instantNow = Instant.now();
        Instant instantDate = date.toInstant();
        Duration duration = Duration.between(instantNow, instantDate);

        long seconds = Math.abs(duration.getSeconds());
        long days = seconds / (60 * 60 * 24);
        long hours = (seconds / (60 * 60)) % 24;
        long minutes = (seconds / 60) % 60;
        seconds = seconds % 60;

        String durationString = String.format("%d days, %d hours, %d minutes, %d seconds", 
