import javax.naming.Context;
import javax.naming.directory.DirContext;
import javax.naming.directory.SearchControls;
import javax.naming.directory.SearchResult;
import javax.naming.ldap.InitialLdapContext;
import javax.naming.ldap.LdapContext;

public class LDAPUtils {

    // LDAP server configuration
    private static final String LDAP_HOST = "ldap.example.com";
    private static final String LDAP_PORT = "389";
    private static final String LDAP_BASE_DN = "dc=example,dc=com";
    private static final String LDAP_ADMIN_USER = "admin@example.com";
    private static final String LDAP_ADMIN_PASSWORD = "password123";

    /**
     * Retrieves the group attributes by group name using the LDAP API.
     *
     * @param groupName the name of the group to retrieve attributes for
     * @return the group attributes as a SearchResult object
     */
    public static SearchResult getGroupAttributes(String groupName) throws Exception {
        // Set up the LDAP context
        LdapContext ldapContext = null;
        try {
            ldapContext = new InitialLdapContext(getLdapEnv(), null);
        } catch (Exception e) {
            throw new Exception("Unable to connect to LDAP server: " + e.getMessage());
        }

        // Set up the LDAP search controls
        SearchControls searchControls = new SearchControls();
        searchControls.setSearchScope(SearchControls.SUBTREE_SCOPE);
        searchControls.setReturningAttributes(new String[]{"cn", "description", "member"});

        // Execute the LDAP search
        String searchFilter = "(&(objectClass=groupOfNames)(cn=" + groupName + "))";
        SearchResult searchResult = null;
        try {
            searchResult = ldapContext.search(LDAP_BASE_DN, searchFilter, searchControls).next();
        } catch (Exception e) {
            throw new Exception("Unable to retrieve group attributes: " + e.getMessage());
        }

        // Close the LDAP context
        ldapContext.close();

        return searchResult;
    }

    /**
     * Returns the LDAP environment properties for connecting to the LDAP server.
     */
    private static Hashtable<String, Object> getLdapEnv() {
        Hashtable<String, Object> env = new Hashtable<>();
        env.put(Context.INITIAL_CONTEXT_FACTORY, "com.sun.jndi.ldap.LdapCtxFactory");
        env.put(Context.PROVIDER_URL, "ldap://" + LDAP_HOST + ":" + LDAP_PORT + "/" + LDAP_BASE_DN);
        env.put(Context.SECURITY_AUTHENTICATION, "simple");
        env.put(Context.SECURITY_PRINCIPAL, LDAP_ADMIN_USER);
        env.put(Context.SECURITY_CREDENTIALS, LDAP_ADMIN_PASSWORD);
        return env;
    }
}

