public static RealMatrix convertToBlockMatrix(double[][] A, int blockSize) {
    int n = A.length;
    int m = A[0].length;
    int numBlockRows = (n + blockSize - 1) / blockSize;
    int numBlockCols = (m + blockSize - 1) / blockSize;
    RealMatrix blockMatrix = new BlockRealMatrix(numBlockRows, numBlockCols, blockSize, blockSize);
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
