public static int getMaxLifetimeInSeconds(Element lifetimeElement) throws Exception {
    int maxLifetimeInSeconds = -1;
    if (lifetimeElement != null) {
        NodeList lifetimeNodes = lifetimeElement.getChildNodes();
        for (int i = 0; i < lifetimeNodes.getLength(); i++) {
            Node lifetimeNode = lifetimeNodes.item(i);
            if (lifetimeNode.getNodeType() == Node.ELEMENT_NODE) {
                Element lifetimeSubElement = (Element) lifetimeNode;
                if ("Expires".equals(lifetimeSubElement.getLocalName())) {
                    String expiresValue = lifetimeSubElement.getTextContent();
                    DateTimeFormatter formatter = DateTimeFormatter.ISO_INSTANT;
                    Instant expires = Instant.from(formatter.parse(expiresValue));
                    Instant now = Instant.now();
                    Duration duration = Duration.between(now, expires);
                    maxLifetimeInSeconds = (int) duration.getSeconds();
                    break;
                } else if ("Created".equals(lifetimeSubElement.getLocalName())) {
                    String createdValue = lifetimeSubElement.getTextContent();
                    DateTimeFormatter formatter = DateTimeFormatter.ISO_INSTANT;
                    Instant created = Instant.from(formatter.parse(createdValue));
                    Instant now = Instant.now();
                    Duration duration = Duration.between(created, now);
                    maxLifetimeInSeconds = (int) duration.getSeconds();
                }
            }
        }
    }
    return maxLifetimeInSeconds;
}

