import java.lang.reflect.Constructor;
import java.lang.reflect.Field;
import java.lang.reflect.Modifier;
import java.util.HashMap;
import java.util.Map;

public class FactoryGenerator {
    public static Map<String, Object> generateFactoryMapping(Class<?> clazz) throws Exception {
        Map<String, Object> factoryMapping = new HashMap<>();

        // Find all public constructors for the class
        Constructor<?>[] constructors = clazz.getConstructors();
        for (Constructor<?> constructor : constructors) {
            // Check if the constructor is public and has no parameters
            if (Modifier.isPublic(constructor.getModifiers()) && constructor.getParameterCount() == 0) {
                // Instantiate the object using the constructor
                Object object = constructor.newInstance();

                // Find all public fields for the object
                Field[] fields = clazz.getFields();
                for (Field field : fields) {
                    // Check if the field is public and not static
                    if (Modifier.isPublic(field.getModifiers()) && !Modifier.isStatic(field.getModifiers())) {
                        // Add the field and its value to the factory mapping
                        factoryMapping.put(field.getName(), field.get(object));
                    }
                }

                // Add the object to the factory mapping using the class name as the key
                factoryMapping.put(clazz.getSimpleName(), object);
            }
        }

        return factoryMapping;
    }
}

