import java.util.NavigableSet;
import java.util.TreeSet;

public class RangeSet {
    private NavigableSet<Integer> set = new TreeSet<>(); // Use TreeSet to store ranges

    // Add a range to the range set
    public void addRange(int start, int end) {
        set.add(start);
        set.add(end + 1); // Add end + 1 as exclusive upper bound
    }

    // Remove ranges from the range set
    public void removeRanges(int start, int end) {
        // Find overlapping ranges
        Integer lower = set.lower(start); // Returns the greatest element strictly less than start
        Integer higher = set.higher(end); // Returns the least element strictly greater than end

        // If lower bound of overlapping range is found
        if (lower != null && lower < start) {
            set.remove(lower); // Remove lower bound
        }

        // If upper bound of overlapping range is found
        if (higher != null && higher > end) {
            set.remove(higher); // Remove upper bound
        }
    }

    // Print the remaining ranges in the range set
    public void printRanges() {
        System.out.print("Ranges: ");
        boolean isStart = true;
        for (int num : set) {
            if (isStart) {
                System.out.print(num);
                isStart = false;
            } else {
                System.out.print("-" + (num - 1) + " ");
                isStart = true;
            }
        }
        System.out.println();
    }

    public static void main(String[] args) {
        RangeSet rangeSet = new RangeSet();
        rangeSet.addRange(1, 5);
        rangeSet.addRange(10, 15);
        rangeSet.addRange(20, 25);
        rangeSet.printRanges(); // Original ranges: 1-5 10-15 20-25

        rangeSet.removeRanges(3, 12);
        rangeSet.printRanges(); // Ranges after removal: 1-2 13-15 20-25
    }
}

