public class WSAddressingUtil {

    /**
     * Sets the WS-Addressing namespace URI for the specified WebServiceContext.
     *
     * @param context       The WebServiceContext instance.
     * @param namespaceURI  The namespace URI to be set for WS-Addressing.
     * @throws SOAPException If an error occurs while setting the WS-Addressing namespace URI.
     */
    public static void setWSAddressingNamespaceURI(WebServiceContext context, String namespaceURI) throws SOAPException {
        Map<String, Object> requestContext = ContextUtils.getRequestContext(context);
        if (requestContext != null) {
            AddressingProperties maps = (AddressingProperties) requestContext.get(JAXWSAConstants.ADDRESSING_PROPERTIES_INBOUND);
            if (maps != null) {
                maps.exposeAs(namespaceURI);
            }
        }
    }

    /**
     * Exposes the WS-Addressing MAPs (Message Addressing Properties) as SOAP headers with the specified namespace URI.
     *
     * @param namespaceURI  The namespace URI to be set for WS-Addressing.
     * @return The list of SOAP headers containing the exposed WS-Addressing MAPs.
     * @throws SOAPException If an error occurs while exposing the WS-Addressing MAPs.
     */
    public static List<Header> exposeWSAddressingMaps(String namespaceURI) throws SOAPException {
        SOAPFactory soapFactory = SOAPFactory.newInstance();
        SOAPHeaderElement addressingHeader = soapFactory.createElement(new QName(namespaceURI, JAXWSAConstants.WSA_HEADER_NAME));
        AddressingProperties maps = ContextUtils.retrieveMAPs(null, false, false);
        if (maps != null) {
            Element element = maps.getAsElement(soapFactory);
            addressingHeader.addChildElement(element);
        }
        SoapHeader soapHeader = new SoapHeader(addressingHeader);
        return List.of(soapHeader);
    }
}

