import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;

public class ServerResponseReader {

    public static String readServerResponse(String url) throws IOException {
        // Create a URL object with the provided URL string
        URL serverUrl = new URL(url);
        // Open a connection to the server
        HttpURLConnection connection = (HttpURLConnection) serverUrl.openConnection();
        // Set the request method to GET
        connection.setRequestMethod("GET");

        // Get the response code from the server
        int responseCode = connection.getResponseCode();

        // If the response code indicates a successful request (200 - HTTP_OK)
        if (responseCode == HttpURLConnection.HTTP_OK) {
            // Create a BufferedReader to read the response from the server
            BufferedReader in = new BufferedReader(new InputStreamReader(connection.getInputStream()));
            String inputLine;
            StringBuilder response = new StringBuilder();

            // Read the response line by line and append to the StringBuilder
            while ((inputLine = in.readLine()) != null) {
                response.append(inputLine);
            }

            // Close the BufferedReader
            in.close();

            // Return the response as a string
            return response.toString();
        } else {
            // If the response code indicates an error, throw an exception
            throw new IOException("Failed to read server response. Response Code: " + responseCode);
        }
    }

    public static void main(String[] args) {
        try {
            String url = "https://www.example.com/api/data"; // Replace with your server URL
            String response = readServerResponse(url);
            System.out.println("Server Response: " + response);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

