import java.lang.reflect.Module;
import java.lang.reflect.ModuleLayer;
import java.lang.reflect.Method;
import java.lang.reflect.Modifier;

public class ModuleFactoryRemover {
    /**
     * Removes factories for a given module.
     *
     * @param moduleName The name of the module for which factories need to be removed.
     * @throws Exception if there are any errors while removing factories.
     */
    public static void removeFactoriesForModule(String moduleName) throws Exception {
        ModuleLayer moduleLayer = ModuleLayer.boot();
        Module module = moduleLayer.findModule(moduleName).orElseThrow(() -> new RuntimeException("Module not found: " + moduleName));

        // Get the ClassLoader of the module
        ClassLoader moduleClassLoader = module.getClassLoader();

        // Get the factory class name for the module
        String factoryClassName = moduleName + ".Factory";

        // Use reflection to remove the factory
        Class<?> factoryClass = Class.forName(factoryClassName, false, moduleClassLoader);
        Method method = ClassLoader.class.getDeclaredMethod("removeClass", String.class);
        method.setAccessible(true);
        method.invoke(moduleClassLoader, factoryClassName);

        // Verify if the factory class has been removed
        try {
            Class.forName(factoryClassName, false, moduleClassLoader);
            throw new RuntimeException("Failed to remove factory for module: " + moduleName);
        } catch (ClassNotFoundException ex) {
            // Factory class not found, which means it has been removed successfully
        }

        // Verify if the factory class is not accessible from the module
        if (factoryClass.getModule().equals(module) && !Modifier.isDeprecated(factoryClass.getModifiers())) {
            throw new RuntimeException("Failed to remove factory for module: " + moduleName);
        }
    }
}

