public class InterestWriter {

    private Selector selector;
    private SocketChannel socketChannel;

    public InterestWriter(Selector selector, SocketChannel socketChannel) {
        this.selector = selector;
        this.socketChannel = socketChannel;
    }

    // Method to activate interest in writing from a thread other than the selector thread
    public void activateInterestInWriting() throws IOException {
        // Create a byte buffer with the data to be written
        ByteBuffer buffer = ByteBuffer.wrap("Hello, world!".getBytes());

        // Register the socket channel for write interest with the selector
        // This will allow the selector to notify the selector thread that the channel is ready for writing
        socketChannel.register(selector, SelectionKey.OP_WRITE, buffer);
    }
}

