import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.NodeList;

import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;

public class XMLParser {

    /**
     * Accesses a float attribute from an XML element using tag name and attribute name.
     *
     * @param xmlString     The XML string to be parsed
     * @param tagName       The tag name of the element to be searched
     * @param attributeName The attribute name of the float attribute to be accessed
     * @return The float value of the specified attribute, or Float.NaN if not found
     */
    public static float getFloatAttributeValue(String xmlString, String tagName, String attributeName) {
        float result = Float.NaN;

        try {
            // Create a DocumentBuilder
            DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
            DocumentBuilder builder = factory.newDocumentBuilder();

            // Parse the XML string into a Document object
            Document document = builder.parse(xmlString);

            // Get the list of elements with the given tag name
            NodeList nodeList = document.getElementsByTagName(tagName);

            // Iterate through the elements
            for (int i = 0; i < nodeList.getLength(); i++) {
                Element element = (Element) nodeList.item(i);

                // Check if the element has the specified attribute
                if (element.hasAttribute(attributeName)) {
                    // Get the attribute value as a string
                    String attributeValue = element.getAttribute(attributeName);

                    // Convert the string to float
                    result = Float.parseFloat(attributeValue);

                    // Break out of the loop after finding the first matching element
                    break;
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
        }

        return result;
    }

    public static void main(String[] args) {
        // Example XML string
        String xmlString = "<root><element1 attr1=\"1.23\"/><element2 attr1=\"4.56\"/></root>";

        // Call the method to get the float attribute value
        float attributeValue = getFloatAttributeValue(xmlString, "element1", "attr1");
        System.out.println("Float attribute value: " + attributeValue);
    }
}

