import com.jcraft.jsch.Session;
import com.jcraft.jsch.UserInfo;
import com.jcraft.jsch.UIKeyboardInteractive;
import com.jcraft.jsch.UIKeyboardInteractiveUserInfo;
import com.jcraft.jsch.UserInfo;
import com.jcraft.jsch.UserInfo;
import com.jcraft.jsch.UserInfo;

public class SshDataReceivedEventListener {
    public static void main(String[] args) {
        try {
            // Create a JSch instance
            JSch jsch = new JSch();

            // Provide SSH session details (hostname, username, password)
            String hostname = "example.com";
            String username = "your_username";
            String password = "your_password";
            int port = 22;

            // Create a session
            Session session = jsch.getSession(username, hostname, port);
            session.setPassword(password);

            // Disable host key checking
            session.setConfig("StrictHostKeyChecking", "no");

            // Connect to the SSH server
            session.connect();

            // Open a new channel (e.g. ChannelExec)
            Channel channel = session.openChannel("exec");
            ((ChannelExec) channel).setCommand("ls -al");

            // Register a data received event listener
            channel.setInputStream(null);
            channel.setOutputStream(System.out);
            channel.setExtOutputStream(System.err);
            channel.connect();

            // Capture SSH data received event
            channel.setInputStream(System.in);
            channel.setOutputStream(System.out);
            channel.setExtOutputStream(System.err);
            byte[] buffer = new byte[1024];
            int bytesRead = 0;
            while (true) {
                bytesRead = channel.getInputStream().read(buffer);
                if (bytesRead > 0) {
                    String dataReceived = new String(buffer, 0, bytesRead);
                    System.out.println("Data Received: " + dataReceived);
                }
            }

            // Disconnect from the SSH server
            channel.disconnect();
            session.disconnect();

        } catch (JSchException | IOException e) {
            e.printStackTrace();
        }
    }
}

