import java.util.HashMap;
import java.util.Map;

public class FrequencyCounter {
    private Map<String, Integer> frequencyMap;

    public FrequencyCounter() {
        frequencyMap = new HashMap<>();
    }

    // Method to add a key to the frequency counter
    public void addKey(String key) {
        // If key is already present in the frequencyMap, increment its count by 1
        if (frequencyMap.containsKey(key)) {
            frequencyMap.put(key, frequencyMap.get(key) + 1);
        }
        // If key is not present in the frequencyMap, add it with count 1
        else {
            frequencyMap.put(key, 1);
        }
    }

    // Method to get the count of a specified key in the frequency counter
    public int getCount(String key) {
        // If key is present in the frequencyMap, return its count
        if (frequencyMap.containsKey(key)) {
            return frequencyMap.get(key);
        }
        // If key is not present in the frequencyMap, return 0
        else {
