import java.util.*;

class CSP {
    // Variable domain representation
    Map<String, List<Integer>> domains;
    // Binary constraints representation
    List<Constraint> constraints;

    // Constraint class representing binary constraints
    static class Constraint {
        String var1;
        String var2;
        BiPredicate<Integer, Integer> predicate;

        public Constraint(String var1, String var2, BiPredicate<Integer, Integer> predicate) {
            this.var1 = var1;
            this.var2 = var2;
            this.predicate = predicate;
        }
    }

    // Constructor
    public CSP() {
        domains = new HashMap<>();
        constraints = new ArrayList<>();
    }

    // Method to add variables to the CSP
    public void addVariable(String var, List<Integer> domain) {
        domains.put(var, domain);
    }

    // Method to add binary constraints to the CSP
    public void addConstraint(String var1, String var2, BiPredicate<Integer, Integer> predicate) {
        constraints.add(new Constraint(var1, var2, predicate));
    }

    // Method to make the CSP arc-consistent using AC-3 algorithm
    public boolean makeArcConsistent() {
        Queue<Constraint> queue = new LinkedList<>(constraints);

        while (!queue.isEmpty()) {
            Constraint constraint = queue.poll();

            if (revise(constraint)) {
                if (domains.get(constraint.var1).isEmpty()) {
                    return false; // Domain empty, no solution possible
                }

                for (Constraint c : constraints) {
                    if (c.var2.equals(constraint.var1)) {
                        queue.offer(c);
                    }
                }
            }
        }

        return true;
    }

    // Helper method to revise the domain of a variable based on a binary constraint
    private boolean revise(Constraint constraint) {
        boolean revised = false;
        List<Integer> domain1 = domains.get(constraint.var1);
        List<Integer> domain2 = domains.get(constraint.var2);

        for (int i = 0; i < domain1.size(); i++) {
            int value1 = domain1.get(i);
            boolean consistent = false;

            for (int j = 0; j < domain2.size(); j++) {
                int value2 = domain2.get(j);

                if (constraint.predicate.test(value1, value2)) {
                    consistent = true;
                    break;
                }
            }

            if (!consistent) {
                domain1.remove(i);
                revised = true;
                i--;
            }
        }

        return revised;
    }
}

