            fileWriter.close();
        } catch (IOException e) {
            System.err.println("Failed to write boolean to file: " + e.getMessage());
        } finally {
            // Close the FileWriter in case of exception
            if (fileWriter != null) {
                try {
                    fileWriter.close();
                } catch (IOException e) {
                    System.err.println("Failed to close file writer: " + e.getMessage());
                }
            }
        }
    }
}

