import javafx.application.Platform;
import java.util.concurrent.Callable;

public class JavaFXUtils {
    public static <T> T invokeAndWait(Callable<T> callable) throws Exception {
        if (Platform.isFxApplicationThread()) {
            // If already on JavaFX thread, simply call the callable and return the result
            return callable.call();
        } else {
            // If not on JavaFX thread, use Platform.runLater to execute the callable and wait for completion
            final Object syncObject = new Object();
            final Throwable[] throwable = new Throwable[1];
            final Object[] result = new Object[1];

            Platform.runLater(() -> {
                try {
                    result[0] = callable.call();
                } catch (Throwable t) {
                    throwable[0] = t;
                } finally {
                    synchronized (syncObject) {
                        syncObject.notify();
                    }
                }
            });

            synchronized (syncObject) {
                syncObject.wait();
            }

            if (throwable[0] != null) {
                // If an exception was thrown during the execution of the callable, re-throw it
                throw new Exception("Exception occurred during execution of the Callable", throwable[0]);
            }

            // Return the result of the callable
            return (T) result[0];
        }
    }
}

