public static void setArrayLength(int[] arr, int newLength) {
    if (newLength < 0) {
        throw new IllegalArgumentException("New length must be non-negative.");
    }

    if (newLength == arr.length) {
        return; // No need to resize if new length is same as current length
    }

    int[] newArr = new int[newLength]; // Create a new array with the new length

    // Copy elements from the original array to the new array, up to the minimum of the original length and new length
    int min = Math.min(arr.length, newLength);
    System.arraycopy(arr, 0, newArr, 0, min);

    // Update the reference to the original array to point to the new array
    arr = newArr;
}

