public class ResourceReader {

    public static Reader getResourceAsReader(String filePath) throws IOException {
        // Get the class loader for the current class
        ClassLoader classLoader = ResourceReader.class.getClassLoader();

        // Load the resource as an InputStream
        InputStream inputStream = classLoader.getResourceAsStream(filePath);

        if (inputStream == null) {
            throw new IOException("Resource not found: " + filePath);
        }

        // Create a Reader from the InputStream using UTF-8 encoding
        Reader reader = new InputStreamReader(inputStream, "UTF-8");

        return reader;
    }

    public static void main(String[] args) {
