            String filePath = "example.txt"; // Specify the file path of the resource
            Reader reader = getResourceAsReader(filePath);

            // Read from the resource using the returned Reader
            int ch;
            while ((ch = reader.read()) != -1) {
                System.out.print((char) ch);
            }

            // Close the Reader after reading
            reader.close();
        } catch (IOException e) {
            e.printStackTrace();
